package com.atguigu.kafka.producer;

import kafka.javaapi.producer.Producer;
import kafka.producer.KeyedMessage;
import kafka.producer.ProducerConfig;

import java.util.Properties;

public class OldProducer {
    public static void main(String[] args) {

        /*创建 Properties 对象*/
        Properties props = new Properties();
        props.put("metadata.broker.list", "hadoop201:9092");
        props.put("request.required.acks", "1");
        props.put("serializer.class", "kafka.serializer.StringEncoder");
        /*生产者配置文件对象*/
        ProducerConfig conf = new ProducerConfig(props);
        /*1. 创建 生产者客户端 泛型是要发送的消息的 key, value 类型*/
        Producer producer = new Producer(conf);

        /*创建要发送的消息对象: 参数1: topic 参数2: vlaue */
        KeyedMessage msg = new KeyedMessage("first", "hello atguigu");
        /*2. 发送消息*/
        producer.send(msg);

    }

}


4.2.2 创建生产者(新 API)
package com.atguigu.kafka.producer;

import org.apache.kafka.clients.producer.KafkaProducer;
import org.apache.kafka.clients.producer.Producer;
import org.apache.kafka.clients.producer.ProducerRecord;

import java.util.Properties;

public class NewProducer {
    public static void main(String[] args) {
        Properties props = new Properties();
        // Kafka服务端的主机名和端口号
        props.put("bootstrap.servers", "hadoop201:9092");
        // 等待所有副本节点的应答
        props.put("acks", "all");
        // 重试最大次数
        props.put("retries", 0);
        // 批消息处理大小
        props.put("batch.size", 16384);
        // 请求延时
        props.put("linger.ms", 1);
        //// 发送缓存区内存大小
        props.put("buffer.memory", 33554432);
        // key序列化
        props.put("key.serializer", "org.apache.kafka.common.serialization.StringSerializer");
        // value序列化
        props.put("value.serializer", "org.apache.kafka.common.serialization.StringSerializer");
        Producer producer = new KafkaProducer<>(props);
        for (int i = 0; i ("first", i + "", "atguigu " + i));
        }
        // 数据发送完毕后关闭生产者与kafka服务器的连接
        producer.close();
    }
}


